import React, { useState } from "react";
import "./styles/App.css";

const HomePage = () => {
  const [location, setLocation] = useState({ latitude: null, longitude: null });
  const [error, setError] = useState("");

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
          setError("");
        },
        (err) => {
          switch (err.code) {
            case err.PERMISSION_DENIED:
              setError("User denied the request for Geolocation.");
              break;
            case err.POSITION_UNAVAILABLE:
              setError("Location information is unavailable.");
              break;
            case err.TIMEOUT:
              setError("The request to get user location timed out.");
              break;
            default:
              setError("An unknown error occurred.");
          }
        }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  };

  return (
    <div className="container">
      <h1>Welcome to the Tourist App</h1>
      <button className="button" onClick={getLocation}>
        Show My Location
      </button>
      {error && <p id="demo" style={{ color: "red" }}>{error}</p>}
      {location.latitude && location.longitude && (
        <p id="demo">
          Latitude: {location.latitude}
          <br />
          Longitude: {location.longitude}
        </p>
      )}
      <div style={{ height: "400px", width: "100%", marginTop: "20px" }}>
        <iframe
          title="Google Map"
          width="100%"
          height="100%"
          frameBorder="0"
          style={{ border: 0 }}
          src={`https://www.google.com/maps/embed/v1/view?key=AIzaSyClqRd5uLObGLdbF-KDkaLO1xEN6X0grOc&center=${location.latitude || 32.9859827},${location.longitude || -96.7512878}&zoom=10`}
          allowFullScreen
        ></iframe>
      </div>
    </div>
  );
};

export default HomePage;
