import React, { useState, useEffect } from "react";
import "../styles/App.css";

const HomePage = () => {
  const [location, setLocation] = useState({ latitude: 32.9859827, longitude: -96.7512878 });
  const [error, setError] = useState("");

  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
          setError("");
        },
        (err) => {
          switch (err.code) {
            case err.PERMISSION_DENIED:
              setError("User denied the request for Geolocation.");
              break;
            case err.POSITION_UNAVAILABLE:
              setError("Location information is unavailable.");
              break;
            case err.TIMEOUT:
              setError("The request to get user location timed out.");
              break;
            default:
              setError("An unknown error occurred.");
          }
        }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  };

  useEffect(() => {
    if (window.google) {
      const mapElement = document.querySelector("gmp-map");
      if (mapElement) {
        mapElement.setAttribute("center", `${location.latitude},${location.longitude}`);
        const markerElement = document.querySelector("gmp-advanced-marker");
        if (markerElement) {
          markerElement.setAttribute("position", `${location.latitude},${location.longitude}`);
        }
      }
    }
  }, [location]);

  return (
    <div className="container">
      <h1>Welcome to the Tourist App</h1>
      <button className="button" onClick={getLocation}>
        Show My Location
      </button>
      {error && <p id="demo" style={{ color: "red" }}>{error}</p>}
      <div style={{ height: "80%", marginTop: "20px" }}>
        <gmp-map
          center={`${location.latitude},${location.longitude}`}
          zoom="10"
          map-id="DEMO_MAP_ID"
          style={{ height: "100%", width: "100%" }}
        >
          <gmp-advanced-marker
            position={`${location.latitude},${location.longitude}`}
            title="My location"
          ></gmp-advanced-marker>
        </gmp-map>
      </div>
    </div>
  );
};

export default HomePage;
